const DataType = {
    uint8: 0x20,
    uint16: 0x21,
    uint24: 0x22,
    uint32: 0x23,
    enum8: 0x30,
};
const manufacturerOptions = {
    doclab : {manufacturerCode: 0x6464}
};
const zcl_attr_sensor_check_rate = '25601';

const fz_IAS_state = {
//    ias_contact_alarm_1: 
        cluster: 'ssIasZone',
        type: 'commandStatusChangeNotification',
        convert: (model, msg, publish, options, meta) => {
            const zoneStatus = msg.data.zonestatus;
//            const contactProperty = postfixWithEndpointName('contact', msg, model, meta);
//            const tamperProperty = postfixWithEndpointName('tamper', msg, model, meta);
//            const batteryLowProperty = postfixWithEndpointName('battery_low', msg, model, meta);
//            const testProperty = postfixWithEndpointName('test', msg, model, meta);
//            const batteryDefectProperty = postfixWithEndpointName('battery_defect', msg, model, meta);
            return {
                contact:        !((zoneStatus & 1) > 0),
                tamper:         (zoneStatus & 1<<2) > 0,
                battery_low:    (zoneStatus & 1<<3) > 0,
                test:           (zoneStatus & 1<<8) > 0,
                battery_defect: (zoneStatus & 1<<9) > 0,
            };
        },
    };
const fz_Rates = {
    cluster: 'genPollCtrl',
    type: ['attributeReport', 'readResponse'],
    convert: (model, msg, publish, options, meta) => {
        const result = {};

        if(msg.data.hasOwnProperty('checkinInterval')) {
            result['checkin_rate'] = msg.data['checkinInterval'];
        }
        if(msg.data.hasOwnProperty('shortPollInterval')) {
            result['short_poll_rate'] = msg.data['shortPollInterval'];
        }
        if(msg.data.hasOwnProperty('longPollInterval')) {
            result['long_poll_rate'] = msg.data['longPollInterval'];
        }
        if(msg.data.hasOwnProperty(zcl_attr_sensor_check_rate)) {
            result['sensor_check_rate'] = msg.data[zcl_attr_sensor_check_rate];
        }

        return result;
       }
}
const tz_Rates = {
    key: ['checkin_rate', 'short_poll_rate', 'long_poll_rate', 'sensor_check_rate'],
    convertGet: async (entity, key, meta) => {
        switch(key) {
        case 'checkin_rate':
            entity.read('genPollCtrl',['checkinInterval']);
            break;
        case 'short_poll_rate':
            entity.read('genPollCtrl',['shortPollInterval']);
            break;
        case 'long_poll_rate':
            entity.read('genPollCtrl',['longPollInterval']);
            break;
        case 'sensor_check_rate ':
            entity.read('genPollCtrl',[zcl_attr_sensor_check_rate]);
            break;
        }

   },
    convertSet: async (entity, key, value, meta) => {
        let payload = {};
        let newValue = value;

        switch(key) {
            case 'checkin_rate':
                //payload = {65282: {'value': value, 'type': DataType.uint32}};
                await entity.write('genPollCtrl', {'checkinInterval': (value)});
                //await entity.write('genBasic', payload, manufacturerOptions.doclab);
                break;
            case 'short_poll_rate':
                await entity.command('genPollCtrl', 'setShortPollInterval', {'newShortPollInterval': (value)}); 
                break;
            case 'long_poll_rate':
                await entity.command('genPollCtrl', 'setLongPollInterval', {'newLongPollInterval': (value)}); 
                break;
            case 'sensor_check_rate':
                payload = {25601: {'value': value, 'type': DataType.uint32}};
                //await entity.write('genPollCtrl', {'checkinInterval': (value)});
                await entity.write('genPollCtrl', payload);//, manufacturerOptions.doclab);
                break;

        }
    }
}
const fz_Switch_config = {
        cluster: 'genOnOffSwitchCfg',
        type: ['readResponse'],
        convert: (model, msg, publish, options, meta) => {
            //const button = getKey(model.endpoint(msg.device), msg.endpoint.ID);
            const {switchActions, switchType} = msg.data;
            const switchTypesLookup = ['toggle', 'momentary', 'multifunction'];
            const switchActionsLookup = ['on_off', 'off_on', 'toggle'];
            return {
                [`switch_type`]: switchTypesLookup[switchType],
                [`switch_actions`]: switchActionsLookup[switchActions],
                //[`switch_type_${button}`]: switchTypesLookup[switchType],
                //[`switch_actions_${button}`]: switchActionsLookup[switchActions],
            }
        }
}
const tz_Switch_config = {
        key: ['switch_type', 'switch_actions'],
        convertGet: async (entity, key, meta) => {
            await entity.read('genOnOffSwitchCfg', ['switchType', 'switchActions']);
        },
        convertSet: async (entity, key, value, meta) => {
            const switchTypesLookup = {
                toggle: 0x00,
                momentary: 0x01,
                multifunction: 0x02,
            };
            const switchActionsLookup = {
                on_off: 0x00,
                off_on: 0x01,
                toggle: 0x02,
            };
            const intVal = parseInt(value, 10);
            const switchType = switchTypesLookup.hasOwnProperty(value) ? switchTypesLookup[value] : intVal;
            const switchActions = switchActionsLookup.hasOwnProperty(value) ? switchActionsLookup[value] : intVal;

            const payloads = {
                switch_type: {switchType},
                switch_actions: {switchActions},
            };
            //meta.logger.debug(`key ${key} value ${value} switchType ${switchType} switchActions ${switchActions} `);
            await entity.write('genOnOffSwitchCfg', payloads[key]);

            return {state: {[`${key}`]: value}};
        }
}
